class OBJECT extends PropEvents {
  constructor (sourceSchema = {}, { values, parent, name } = {}) {
    super(parent)
    const [schema, schemaValues] = schemaBuilder(sourceSchema)
    // console.log(schemaValues)
    this.schema = schema
    // if (parent && name) {
    //   parent[name] = this
    //   this.parent = parent
    //   this.name = name
    // }
    this.buildFromSchema(schema)
    this.assign(values || schemaValues)

    if (parent && name) {
      this.name = name
      Object.defineProperty(parent, name, {
        get: () => {
          return this
        },
        set: (v) => {
          this.assign(v)
        }
      })
    }
    this._pKey = this.schema._id
      ? '_id'
      : (
          this.schema.id ? 'id' : Object.keys(this.schema).shift()
        )
  }

  assign (values, { preventEvent = false } = {}) {
    // console.log(values)
    if (values) {
      Object.keys(values).forEach(prop => {
        // if (prop === 'friend') console.log({ friend: values[prop] })
        this[prop]._set(values[prop], { preventEvent })
      })
    }
  }

  _set (value, { preventEvent = false } = {}) {
    this.assign(value, { preventEvent })
  }

  buildFromSchema (schema) {
    this._values = {}
    Object.entries(schema).forEach(([key, Prop]) => {
      // console.log(value)
      // const [Prop, Value] = getProp(value)
      // console.log(Prop, Value)
      if (Prop) {
        if (typesFoundInSchema.includes(Prop) || Prop.isObservable) {
          this._values[key] = new Prop(null, {
            parent: this,
            name: key
          })
        } else if (Prop instanceof Array) {
          this._values[key] = new ARRAY(Prop[0], {
            parent: this,
            name: key
          })
        } else {
          this._values[key] = new OBJECT(Prop, {
            parent: this,
            name: key
          })
        }
      }
    })
  }

  toJSON () {
    return this._values
  }
}
export default OBJECT

import Prop from './classes/Prop.js'
import { LOAD } from './events/events.js'
import { referenceModel } from './containers/models.js'
class REFERENCE extends Prop {
  constructor (model, { id, parent, name } = {}) {
    super(id, { parent, name })
    if (Object.keys(model).length !== 1) throw new Error('Reference model in constructor must have one key e.g. {User}')
    this.modelName = Object.keys(model).pop()
    this.model = model[this.modelName]
    this._ref = null
    if (id) {
      referenceModel(this.modelName, id).then(ref => {
        this._attachRef(ref)
      })
    }

    if (parent && name) {
      Object.defineProperty(parent, `_${name}`, {
        get: () => {
          return this._ref
        }
      })
    }
  }

  _preChange () {
    this._ref = null
    // console.log({id:this.value})
    if (this.value) {
      referenceModel(this.modelName, this.value).then(ref => {
        this._attachRef(ref)
        // this.processEvent(LOAD, this, { bubble: true })
      })
    }
  }

  getRef () {
    if (this._ref) {
      return Promise.resolve(this._ref)
    } else {
      return new Promise((resolve) => {
        this.on(LOAD, () => {
          resolve(this._ref)
        })
      })
    }
  }

  _attachRef (ref) {
    this._ref = ref
    this.processEvent(LOAD, this, { bubble: true })
  }

  get () {
    return this._ref
  }

  valueOf () {
    return this.value
  }
}

export default REFERENCE

